// Extrair resposta da OpenAI
const response = $input.item.json.message?.content || $input.item.json.response || '';

if (!response || response.trim().length < 100) {
  throw new Error('Resposta da OpenAI est√° vazia ou muito curta');
}

// Fun√ß√£o para extrair se√ß√£o do markdown
function extractSection(text, sectionName) {
  const regex = new RegExp(`##\\s*[üé£üìñüéÜüì¢]*\\s*${sectionName}[\\s\\S]*?(?=##|$)`, 'i');
  const match = text.match(regex);
  return match ? match[0].replace(/##[^\n]+\n/, '').trim() : '';
}

// Extrair se√ß√µes principais
const gancho = extractSection(response, 'GANCHO');
const desenvolvimento = extractSection(response, 'DESENVOLVIMENTO');
const climax = extractSection(response, 'CL√çMAX|CLIMAX');
const cta = extractSection(response, 'CTA|Call to Action');

// Extrair metadados
const hashtagsMatch = response.match(/\*\*Hashtags:\*\*\s*([^\n]+)/);
const duracaoMatch = response.match(/\*\*Dura√ß√£o estimada:\*\*\s*([^\n]+)/);
const tomMatch = response.match(/\*\*Tom predominante:\*\*\s*([^\n]+)/);
const plataformaMatch = response.match(/\*\*Plataforma ideal:\*\*\s*([^\n]+)/);
const momentoMatch = response.match(/\*\*Momento ideal:\*\*\s*([^\n]+)/);
const publicoMatch = response.match(/\*\*P√∫blico-alvo:\*\*\s*([^\n]+)/);

// Extrair t√≠tulo (primeira linha com #)
const tituloMatch = response.match(/^#\s+(.+)$/m);
const tituloPub = tituloMatch ? tituloMatch[1].replace(/[üé¨üìåüí°üî•‚ú®]/g, '').trim() : $('Preparar Contexto Roteiro').item.json.titulo;

// Processar hashtags
const hashtagsRaw = hashtagsMatch ? hashtagsMatch[1] : '';
const hashtags = hashtagsRaw
  .split(/[\s,]+/)
  .filter(h => h.startsWith('#'))
  .map(h => h.replace('#', '').toLowerCase())
  .slice(0, 10); // M√°ximo 10 hashtags

// Calcular m√©tricas do roteiro
const palavrasTotal = response.split(/\s+/).length;
const palavrasNarracao = (gancho + ' ' + desenvolvimento + ' ' + climax + ' ' + cta).split(/\s+/).length;
const duracaoSegundos = Math.round(palavrasNarracao / 2.5); // ~150 palavras/minuto

// Extrair elementos visuais sugeridos
const elementosVisuais = [...response.matchAll(/\[([^\]]+)\]/g)].map(m => m[1]);

// Calcular scores de qualidade
const temGancho = gancho.length > 50;
const temDesenvolvimento = desenvolvimento.length > 200;
const temClimax = climax.length > 50;
const temCTA = cta.length > 20;
const qualityScore = [temGancho, temDesenvolvimento, temClimax, temCTA].filter(Boolean).length * 25;

// Contexto herdado
const contexto = $('Preparar Contexto Roteiro').item.json;

return {
  json: {
    // Roteiro completo
    roteiro_markdown: response,
    titulo_publico: tituloPub,

    // Se√ß√µes estruturadas
    secoes: {
      gancho: gancho,
      desenvolvimento: desenvolvimento,
      climax: climax,
      cta: cta
    },

    // Metadados extra√≠dos
    hashtags: hashtags,
    tom_narrativo: tomMatch ? tomMatch[1].trim() : 'narrativo',
    plataforma_foco: plataformaMatch ? plataformaMatch[1].trim().toLowerCase() : 'tiktok',
    momento_ideal: momentoMatch ? momentoMatch[1].trim() : 'qualquer',
    publico_alvo: publicoMatch ? publicoMatch[1].trim() : 'geral',

    // M√©tricas
    duracao_segundos: duracaoSegundos,
    palavras_total: palavrasTotal,
    palavras_narracao: palavrasNarracao,
    elementos_visuais: elementosVisuais,
    quality_score: qualityScore,

    // Valida√ß√µes
    validacoes: {
      tem_gancho: temGancho,
      tem_desenvolvimento: temDesenvolvimento,
      tem_climax: temClimax,
      tem_cta: temCTA,
      duracao_ok: duracaoSegundos >= 40 && duracaoSegundos <= 65,
      palavras_ok: palavrasNarracao >= 110 && palavrasNarracao <= 150
    },

    // Contexto da ideia
    ideia_id: contexto.ideia_id,
    canal_id: contexto.canal_id,
    serie_id: contexto.serie_id,
    linguagem: contexto.linguagem,
    execution_id: contexto.execution_id,

    // Metadados de gera√ß√£o
    gerado_em: new Date().toISOString(),
    modelo_ai: 'gpt-4o',
    temperatura: 0.75,
    max_tokens: 2000
  }
};

