// ==================================================
// LIMPEZA DE MARKDOWN PARA TTS
// ==================================================

const item = $input.item.json;
const markdown = item.conteudo_md;

if (!markdown || markdown.trim().length < 50) {
  throw new Error(`Roteiro ${item.roteiro_id}: conteúdo muito curto ou vazio`);
}

// ========== LIMPEZA DO MARKDOWN ==========
let textoLimpo = markdown
  // Remover metadados YAML/frontmatter (--- ... ---)
  .replace(/^---[\s\S]*?---/m, '')
  // Remover títulos markdown (# ## ###)
  .replace(/^#{1,6}\s+/gm, '')
  // Remover bold/italic markdown
  .replace(/\*\*(.+?)\*\*/g, '$1')
  .replace(/\*(.+?)\*/g, '$1')
  .replace(/__(.+?)__/g, '$1')
  .replace(/_(.+?)_/g, '$1')
  // Remover links [texto](url) -> manter só texto
  .replace(/\[(.+?)\]\(.+?\)/g, '$1')
  // Remover imagens ![alt](url)
  .replace(/!\[.*?\]\(.+?\)/g, '')
  // Remover listas markdown (-, *, +)
  .replace(/^[-*+]\s+/gm, '')
  // Remover listas numeradas
  .replace(/^\d+\.\s+/gm, '')
  // Remover código inline `code`
  .replace(/`(.+?)`/g, '$1')
  // Remover blocos de código ```code```
  .replace(/```[\s\S]*?```/g, '')
  // Remover blockquotes
  .replace(/^>\s+/gm, '')
  // Remover linhas horizontais
  .replace(/^[-*_]{3,}$/gm, '')
  // Remover múltiplas quebras de linha
  .replace(/\n{3,}/g, '\n\n')
  .trim();

// ========== NORMALIZAÇÃO PARA PRONÚNCIA ==========
textoLimpo = textoLimpo
  // Números e unidades
  .replace(/\b([0-9]+)\s*%/g, '$1 por cento')
  .replace(/\b([0-9]+)\s*km\b/gi, '$1 quilômetros')
  .replace(/\b([0-9]+)\s*m\b(?!\w)/gi, '$1 metros')  // Evitar 'minutos'
  .replace(/\b([0-9]+)\s*kg\b/gi, '$1 quilogramas')
  .replace(/\b([0-9]+)\s*h\b(?!\w)/gi, '$1 horas')
  .replace(/\$\s*([0-9,.]+)/g, '$1 dólares')
  .replace(/R\$\s*([0-9,.]+)/g, '$1 reais')
  // Abreviações comuns
  .replace(/\bDr\./gi, 'Doutor')
  .replace(/\bDra\./gi, 'Doutora')
  .replace(/\bSr\./gi, 'Senhor')
  .replace(/\bSra\./gi, 'Senhora')
  .replace(/\bProf\./gi, 'Professor')
  .replace(/\bEUA\b/g, 'Estados Unidos')
  .replace(/\bONU\b/g, 'ONU')  // Manter sigla
  .replace(/\bNASA\b/g, 'NASA')
  // URLs e emails (remover)
  .replace(/https?:\/\/\S+/g, '')
  .replace(/\S+@\S+\.\S+/g, '')
  // Limpar espaços extras
  .replace(/\s+/g, ' ')
  .trim();

// ========== VALIDAÇÃO DE TAMANHO ==========
const totalCaracteres = textoLimpo.length;
const maxCaracteresTTS = 4000;  // Limite seguro OpenAI TTS
const precisaSplit = totalCaracteres > maxCaracteresTTS;

let chunks = [];
if (precisaSplit) {
  // Dividir em chunks por parágrafos
  const paragrafos = textoLimpo.split(/\n\n+/);
  let chunkAtual = '';

  for (const p of paragrafos) {
    if ((chunkAtual + p).length > maxCaracteresTTS) {
      if (chunkAtual) chunks.push(chunkAtual.trim());
      chunkAtual = p;
    } else {
      chunkAtual += (chunkAtual ? '\n\n' : '') + p;
    }
  }
  if (chunkAtual) chunks.push(chunkAtual.trim());
} else {
  chunks = [textoLimpo];
}

// ========== CUSTO ESTIMADO ==========
// OpenAI TTS-1-HD: $15.00 / 1M caracteres
const custoEstimado = (totalCaracteres / 1000000) * 15;

// ========== OUTPUT ==========
return {
  json: {
    // IDs e referências
    roteiro_id: item.roteiro_id,
    ideia_id: item.ideia_id,
    canal_id: item.canal_id,
    serie_id: item.serie_id,
    pipeline_id: item.pipeline_id,

    // Textos
    titulo: item.titulo,
    texto_original: markdown,
    texto_tts: textoLimpo,
    chunks: chunks,
    total_chunks: chunks.length,

    // Métricas
    total_caracteres: totalCaracteres,
    custo_estimado_usd: parseFloat(custoEstimado.toFixed(4)),
    precisa_split: precisaSplit,

    // Metadata do roteiro
    linguagem: item.linguagem || item.canal_linguagem || 'pt-BR',
    duracao_estimada_segundos: item.duracao_estimado_segundos || 60,

    // Contexto
    canal_nome: item.canal_nome,
    canal_slug: item.canal_slug,
    ideia_titulo: item.ideia_titulo
  }
};

// ==================================================
// SELEÇÃO DE VOZ BASEADA NO IDIOMA
// ==================================================

const item = $input.item.json;
const linguagem = (item.linguagem || 'pt-BR').toLowerCase();

// ========== MAPEAMENTO DE VOZES POR IDIOMA ==========
// Vozes OpenAI: alloy, echo, fable, onyx, nova, shimmer
const vozMapa = {
  // Português
  'pt-br': { voice: 'alloy', speed: 1.0 },
  'pt-pt': { voice: 'alloy', speed: 0.95 },
  'pt': { voice: 'alloy', speed: 1.0 },

  // Inglês
  'en-us': { voice: 'nova', speed: 1.0 },
  'en-gb': { voice: 'shimmer', speed: 0.95 },
  'en': { voice: 'nova', speed: 1.0 },

  // Espanhol
  'es-es': { voice: 'fable', speed: 1.0 },
  'es-mx': { voice: 'onyx', speed: 1.0 },
  'es': { voice: 'fable', speed: 1.0 },

  // Francês
  'fr-fr': { voice: 'echo', speed: 0.95 },
  'fr': { voice: 'echo', speed: 0.95 },

  // Alemão
  'de-de': { voice: 'onyx', speed: 0.95 },
  'de': { voice: 'onyx', speed: 0.95 },

  // Italiano
  'it-it': { voice: 'fable', speed: 1.0 },
  'it': { voice: 'fable', speed: 1.0 },

  // Mandarim
  'zh-cn': { voice: 'nova', speed: 0.9 },
  'zh-tw': { voice: 'shimmer', speed: 0.9 },
  'zh': { voice: 'nova', speed: 0.9 },

  // Japonês
  'ja-jp': { voice: 'echo', speed: 0.9 },
  'ja': { voice: 'echo', speed: 0.9 },

  // Coreano
  'ko-kr': { voice: 'nova', speed: 0.95 },
  'ko': { voice: 'nova', speed: 0.95 }
};

// Buscar configuração ou usar padrão
const config = vozMapa[linguagem] || { voice: 'alloy', speed: 1.0 };

// ========== CONFIGURAÇÃO DO TTS ==========
const modelo = 'tts-1-hd';  // Melhor qualidade
const formato = 'mp3';       // Formato padrão

// ========== PREPARAR CHUNKS PARA PROCESSAMENTO ==========
const chunks = item.chunks || [item.texto_tts];
const totalChunks = chunks.length;

// Preparar array de jobs (um por chunk)
const jobs = chunks.map((texto, index) => ({
  chunk_index: index + 1,
  total_chunks: totalChunks,
  texto: texto,
  caracteres: texto.length,
  voice: config.voice,
  speed: config.speed,
  modelo: modelo,
  formato: formato
}));

// ========== OUTPUT ==========
return {
  json: {
    // Dados do roteiro (passar adiante)
    roteiro_id: item.roteiro_id,
    ideia_id: item.ideia_id,
    canal_id: item.canal_id,
    serie_id: item.serie_id,
    pipeline_id: item.pipeline_id,
    titulo: item.titulo,
    linguagem: item.linguagem,
    duracao_estimada_segundos: item.duracao_estimada_segundos,
    total_caracteres: item.total_caracteres,
    custo_estimado_usd: item.custo_estimado_usd,

    // Configuração TTS
    voice: config.voice,
    speed: config.speed,
    modelo: modelo,
    formato: formato,

    // Chunks para processar
    total_chunks: totalChunks,
    precisa_merge: totalChunks > 1,
    jobs: jobs,

    // Contexto
    canal_nome: item.canal_nome,
    canal_slug: item.canal_slug
  }
};

