// ==================================================
// PREPARAR UPLOAD PARA SUPABASE STORAGE
// ==================================================

const item = $input.item.json;
const binary = $input.item.binary;

if (!binary || !binary.data) {
  throw new Error('Áudio não foi gerado pela OpenAI');
}

// ========== DEFINIR NOME E PATH ==========
const roteiroId = item.roteiro_id || $('Limpar Markdown para TTS').item.json.roteiro_id;
const chunkIndex = item.chunk_index;
const isChunked = chunkIndex !== undefined;

// Nome do arquivo
const filename = isChunked 
  ? `${roteiroId}_chunk${chunkIndex}.mp3`
  : `${roteiroId}.mp3`;

// Path no storage (organizado por roteiro)
const storagePath = `audios/${filename}`;

// ========== METADATA DO ARQUIVO ==========
const metadata = {
  roteiro_id: roteiroId,
  content_type: 'audio/mpeg',
  cache_control: 'public, max-age=31536000',
  is_chunk: isChunked,
  chunk_index: chunkIndex || 1,
  generated_at: new Date().toISOString()
};

// ========== OUTPUT ==========
return {
  json: {
    roteiro_id: roteiroId,
    filename: filename,
    storage_path: storagePath,
    metadata: metadata,
    is_chunk: isChunked,
    chunk_index: chunkIndex || 1
  },
  binary: {
    data: binary.data
  }
};

// ==================================================
// PREPARAR METADATA FINAL DO ÁUDIO
// ==================================================

const roteiroData = $('Limpar Markdown para TTS').item.json;
const vozData = $('Selecionar Voz por Idioma').item.json;
const uploadData = $input.item.json;

// ========== URL PÚBLICA DO STORAGE ==========
const projectId = 'nlcisbfdiokmipyihtuz';
const storagePath = uploadData.storage_path || uploadData.path;
const publicUrl = `https://${projectId}.supabase.co/storage/v1/object/public/${storagePath}`;

// ========== CALCULAR DURAÇÃO REAL ==========
// Aproximação: OpenAI TTS ~150 caracteres/segundo (velocidade 1.0)
const caracteresTotal = roteiroData.total_caracteres;
const velocidade = vozData.speed || 1.0;
const duracaoEstimadaSegundos = Math.ceil((caracteresTotal / 150) / velocidade);

// ========== METADATA COMPLETA ==========
const metadata = {
  // Geração
  provedor: 'openai',
  modelo: vozData.modelo,
  voice: vozData.voice,
  speed: vozData.speed,

  // Métricas
  caracteres: caracteresTotal,
  custo_usd: roteiroData.custo_estimado_usd,
  duracao_estimada_segundos: duracaoEstimadaSegundos,

  // Chunks (se aplicável)
  total_chunks: vozData.total_chunks || 1,
  precisa_merge: vozData.precisa_merge || false,

  // Timestamps
  gerado_em: new Date().toISOString(),
  workflow_execution_id: $execution.id
};

// ========== OUTPUT ==========
return {
  json: {
    // IDs
    roteiro_id: roteiroData.roteiro_id,
    ideia_id: roteiroData.ideia_id,
    canal_id: roteiroData.canal_id,
    serie_id: roteiroData.serie_id,

    // Storage
    storage_path: storagePath,
    public_url: publicUrl,

    // Audio data
    duracao_segundos: duracaoEstimadaSegundos,
    linguagem: roteiroData.linguagem,
    formato: 'audio/mpeg',
    tipo: 'AUDIO_TTS',
    status: vozData.precisa_merge ? 'AGUARDANDO_MERGE' : 'OK',

    // Metadata
    metadata: metadata,

    // Contexto
    titulo: roteiroData.titulo,
    canal_nome: roteiroData.canal_nome
  }
};
